import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import logo from'./logo/ia.png'
import cloud from'./logo/cloud.png'
import {useState} from'react'; //file upload

function App() {

  const [file,setFile] =useState()

  function handleFile(event){
    setFile(event.target.files[0])
    console.log(file)
  }
  return (
    <div className="App">
      <header className="App-header">
      </header>
      <main>
        <Container fluid >
           <div class="item1">
            <div>
            <img src={logo}
                 width="180" height="90"></img>
                  <p className="d-inline-block"
                  align="centre">Transcription Tool</p>
            </div>
           </div>
        </Container>
        <Container>
          <div class="wrapper">
          <div class="item3">
           <p> Upload a file</p>
           <div class="upload">
           <img src={cloud}
                class="cloud"></img>
            <div class="upbutton">
            <input type ="file" display="none"name="file" onChange={handleFile}></input>
            <Button type="button"
           >Browse</Button>
            <p>upolad a .mp3 or .wav file</p>
            </div>
           </div>
            </div>
          </div>
          </Container>
        </main>
    </div>
  );
}

export default App;
